import React, { useState, useEffect, useRef, useCallback } from 'react';
// Using react-icons for real brand logos
import { DiReact, DiNodejsSmall, DiJavascript1, DiPython, DiGitBranch } from 'react-icons/di';
import { SiTailwindcss, SiMongodb, SiGraphql, SiDocker, SiKubernetes, SiTypescript, SiWebpack, SiVercel, SiRedux, SiFirebase, SiJest, SiAmanzonaws } from 'react-icons/si';
import { FaLaptopCode, FaDatabase, FaAward, FaBook, FaTools, FaCloud, FaPalette, FaTerminal, FaGraduationCap, FaBriefcase, FaCalendarAlt, FaMapPin, FaStar, FaTrophy, FaLayerGroup } from 'react-icons/fa';
import { FaAnglesLeft, FaAnglesRight, FaPlay, FaPause } from 'react-icons/fa6';

// All data and logic are kept within this single file for clarity and conciseness.
const educationData = [
  {
    id: 'lpu-btech',
    degree: "Bachelor of Technology, CSE",
    institution: "Lovely Professional University",
    period: "2023 – 2027",
    status: "Current",
    location: "Punjab, India",
    icon: <FaGraduationCap />,
    description: "Specializing in Computer Science and Engineering with a focus on Full Stack Development",
    grade: "Current CGPA: 7.1/10",
    color: "cyan"
  },
  {
    id: 'lt-internship',
    degree: "Software Engineering Internship",
    institution: "Larsen & Toubro",
    period: "2025 – Present",
    status: "Active",
    location: "Faridabad, Haryana",
    icon: <FaBriefcase />,
    description: "Working on enterprise-level applications and gaining hands-on industry experience",
    grade: "Performance: Excellent",
    color: "green"
  }
];

const skillsData = [
  { id: 'react', name: "React & Next.js", icon: <DiReact />, level: 90, category: "frontend", description: "Building modern web applications" },
  { id: 'node', name: "Node.js & Express", icon: <DiNodejsSmall />, level: 85, category: "backend", description: "Server-side development" },
  { id: 'javascript', name: "JavaScript (ES6+)", icon: <DiJavascript1 />, level: 95, category: "language", description: "Core programming language" },
  { id: 'typescript', name: "TypeScript", icon: <SiTypescript />, level: 80, category: "language", description: "Type-safe development" },
  { id: 'tailwind', name: "Tailwind CSS", icon: <SiTailwindcss />, level: 90, category: "frontend", description: "Utility-first CSS framework" },
  { id: 'uiux', name: "UI/UX Design", icon: <FaLaptopCode />, level: 75, category: "design", description: "User interface design" },
  { id: 'mongodb', name: "MongoDB & SQL", icon: <SiMongodb />, level: 85, category: "database", description: "Database management" },
  { id: 'git', name: "Git & GitHub", icon: <DiGitBranch />, level: 90, category: "tools", description: "Version control systems" },
  { id: 'docker', name: "Docker", icon: <SiDocker />, level: 70, category: "devops", description: "Containerization platform" },
  { id: 'aws', name: "AWS", icon: <SiAmanzonaws />, level: 65, category: "cloud", description: "Cloud computing services" },
  { id: 'python', name: "Python", icon: <DiPython />, level: 88, category: "language", description: "Versatile programming language" },
  { id: 'java', name: "Java", icon: <FaTerminal />, level: 82, category: "language", description: "Object-oriented programming" },
  { id: 'graphql', name: "GraphQL", icon: <SiGraphql />, level: 78, category: "backend", description: "Query language for APIs" },
  { id: 'redux', name: "Redux", icon: <SiRedux />, level: 85, category: "frontend", description: "State management library" },
  { id: 'firebase', name: "Firebase", icon: <SiFirebase />, level: 80, category: "cloud", description: "Backend-as-a-Service platform" },
  { id: 'webpack', name: "Webpack", icon: <SiWebpack />, level: 72, category: "tools", description: "Module bundler" },
  { id: 'jest', name: "Jest & Testing", icon: <SiJest />, level: 75, category: "tools", description: "Testing frameworks" },
  { id: 'kubernetes', name: "Kubernetes", icon: <SiKubernetes />, level: 68, category: "devops", description: "Container orchestration" },
];

const achievements = [
  { 
    id: 'hackathon',
    title: "Hackathon Finalist", 
    year: "2025", 
    icon: <FaTrophy />,
    description: "Competitive programming achievement"
  },
  { 
    id: 'coding',
    title: "Leetcode & CodeChef Expert", 
    icon: <FaStar />,
    description: "Problem-solving expertise"
  },
];

const skillCategories = [
  { id: 'all', name: 'All', icon: <FaLayerGroup />, color: 'cyan' },
  { id: 'frontend', name: 'Frontend', icon: <FaLaptopCode />, color: 'blue' },
  { id: 'backend', name: 'Backend', icon: <FaDatabase />, color: 'green' },
  { id: 'language', name: 'Languages', icon: <FaTerminal />, color: 'purple' },
  { id: 'tools', name: 'Tools', icon: <FaTools />, color: 'orange' },
  { id: 'design', name: 'Design', icon: <FaPalette />, color: 'pink' },
  { id: 'cloud', name: 'Cloud', icon: <FaCloud />, color: 'indigo' },
  { id: 'devops', name: 'DevOps', icon: <FaTools />, color: 'red' }
];

const useIntersectionObserver = (threshold = 0.1) => {
  const [isVisible, setIsVisible] = useState(false);
  const ref = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      { threshold, rootMargin: '50px' }
    );

    const currentRef = ref.current;
    if (currentRef) {
      observer.observe(currentRef);
    }

    return () => {
      if (currentRef) {
        observer.unobserve(currentRef);
      }
    };
  }, [threshold]);

  return [ref, isVisible];
};

const SkillCard = React.memo(({ skill, isHovered, onHover }) => (
  <div
    className="group flex items-center gap-3 bg-slate-800/60 backdrop-blur-sm rounded-lg border border-slate-700 hover:border-cyan-400 transition-all duration-300 p-4 relative overflow-hidden cursor-pointer min-w-fit"
    onMouseEnter={() => onHover(skill.id)}
    onMouseLeave={() => onHover(null)}
    role="button"
    tabIndex={0}
    aria-label={`${skill.name} skill`}
  >
    <div className="absolute inset-0 bg-gradient-to-r from-cyan-500/5 to-blue-500/5 opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
    <div className="relative z-10 flex items-center gap-3 w-full">
      <div className="text-cyan-400 group-hover:text-cyan-300 transition-colors flex-shrink-0">
        <div className="w-6 h-6">
          {skill.icon}
        </div>
      </div>
      <span className="text-white font-medium text-sm group-hover:text-cyan-100 transition-colors whitespace-nowrap">
        {skill.name}
      </span>
    </div>
  </div>
));
SkillCard.displayName = 'SkillCard';

const QualificationsSection = () => {
  const [sectionRef, isVisible] = useIntersectionObserver(0.1);
  const [activeSkillCategory, setActiveSkillCategory] = useState('all');
  const [hoveredSkill, setHoveredSkill] = useState(null);
  const [isAutoScrolling, setIsAutoScrolling] = useState(true);
  const scrollContainerRef = useRef(null);
  const animationFrameRef = useRef(null);

  const filteredSkills = activeSkillCategory === 'all' 
    ? skillsData 
    : skillsData.filter(skill => skill.category === activeSkillCategory);

  const handleScrollControl = useCallback((direction) => {
    if (!scrollContainerRef.current) return;
    setIsAutoScrolling(false);
    scrollContainerRef.current.scrollBy({
      left: direction === 'left' ? -300 : 300,
      behavior: 'smooth'
    });
  }, []);

  const toggleAutoScroll = useCallback(() => {
    setIsAutoScrolling(prev => !prev);
  }, []);

  const handleCategoryChange = useCallback((categoryId) => {
    setActiveSkillCategory(categoryId);
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollLeft = 0;
    }
  }, []);

  return (
    <section
      id="qualifications"
      ref={sectionRef}
      className="relative min-h-screen font-sans py-16 px-4 sm:px-6 md:px-8 bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900"
      role="main"
      aria-label="Qualifications and Skills"
    >
      <div className="absolute inset-0 opacity-5" />

      <div className="max-w-7xl w-full mx-auto relative z-10 space-y-12 md:space-y-16 lg:space-y-20">
        <div className={`transition-all duration-700 ease-out ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-6'}`}>
          <div className="bg-slate-800/60 backdrop-blur-sm rounded-t-xl p-4 border-b border-slate-700/50 shadow-lg">
            <div className="flex items-center justify-between">
              <div className="flex space-x-2">
                <div className="w-3 h-3 bg-red-500 rounded-full" />
                <div className="w-3 h-3 bg-yellow-500 rounded-full" />
                <div className="w-3 h-3 bg-green-500 rounded-full" />
              </div>
              <span className="text-slate-400 text-sm font-mono flex-1 text-center">~/portfolio/qualifications</span>
              <div className="w-8" />
            </div>
          </div>
          <div className="bg-slate-800/40 backdrop-blur-sm rounded-b-xl p-8 border border-slate-700/50 shadow-2xl">
            <h1 className="text-2xl sm:text-4xl md:text-5xl lg:text-6xl font-bold text-center text-white">
              <span className="text-cyan-400">{'<'}</span>
              <span className="bg-gradient-to-r from-cyan-400 via-blue-400 to-purple-400 bg-clip-text text-transparent">
                My Qualifications
              </span>
              <span className="text-cyan-400">{'/>'}</span>
            </h1>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-5 gap-8 lg:gap-12 xl:gap-16 items-start">
          <div className={`col-span-1 lg:col-span-2 space-y-8 transition-all duration-700 ease-out ${isVisible ? 'opacity-100 translate-x-0' : 'opacity-0 -translate-x-6'}`}>
            <div className="space-y-5">
              <h2 className="text-xl sm:text-2xl font-bold text-white flex items-center gap-3">
                <FaBook className="text-cyan-400 w-6 h-6" />
                <span className="text-cyan-400 font-mono">education[]</span>
              </h2>
              <div className="space-y-5">
                {educationData.map((edu) => (
                  <article key={edu.id} className="group bg-slate-800/50 backdrop-blur-sm rounded-xl border border-slate-700 hover:border-cyan-400/60 transition-all duration-300 shadow-lg">
                    <div className="p-6 space-y-4">
                      <div className="flex items-start gap-4">
                        <div className={`text-${edu.color}-400 bg-slate-700/60 p-3 rounded-xl group-hover:bg-${edu.color}-400 group-hover:text-slate-900 transition-all duration-300 flex-shrink-0 shadow-lg`}>
                          <div className="w-5 h-5">{edu.icon}</div>
                        </div>
                        <div className="flex-1 min-w-0 space-y-1">
                          <h3 className="font-bold text-lg text-white truncate">{edu.degree}</h3>
                          <p className="text-slate-300 font-semibold">{edu.institution}</p>
                          <p className="text-sm text-slate-400">{edu.description}</p>
                        </div>
                      </div>
                      <div className="flex items-center justify-between text-xs text-slate-500">
                        <span className="flex items-center gap-1.5"><FaCalendarAlt className="w-3.5 h-3.5" />{edu.period}</span>
                        <span className="flex items-center gap-1.5"><FaMapPin className="w-3.5 h-3.5" />{edu.location}</span>
                        <span className={`px-2 py-0.5 text-xs font-mono rounded-full ${edu.status === 'Current' || edu.status === 'Active' ? 'bg-green-500/20 text-green-400' : 'bg-blue-500/20 text-blue-400'}`}>{edu.status}</span>
                      </div>
                      <p className="text-xs text-cyan-400 font-mono bg-slate-900/30 px-3 py-1.5 rounded-md inline-block">{edu.grade}</p>
                    </div>
                  </article>
                ))}
              </div>
            </div>

            <div className="space-y-5">
              <h2 className="text-lg sm:text-xl font-bold text-white flex items-center gap-3">
                <FaAward className="text-yellow-400 w-6 h-6" />
                <span className="text-yellow-400 font-mono">achievements[]</span>
              </h2>
              <div className="grid gap-4">
                {achievements.map((achievement) => (
                  <article key={achievement.id} className="bg-slate-800/40 backdrop-blur-sm rounded-xl border border-slate-700 hover:border-yellow-400/60 p-4 flex items-center gap-3 transition-all duration-300 shadow-lg hover:shadow-yellow-500/10">
                    <div className="text-yellow-400 flex-shrink-0 bg-slate-700/60 p-2 rounded-lg"><div className="w-5 h-5">{achievement.icon}</div></div>
                    <div className="min-w-0">
                      <h3 className="text-white font-semibold text-sm sm:text-base">{achievement.title}</h3>
                      <p className="text-slate-400 text-xs sm:text-sm">{achievement.description}</p>
                      {achievement.year && <p className="text-slate-500 text-xs mt-1">{achievement.year}</p>}
                    </div>
                  </article>
                ))}
              </div>
            </div>
          </div>

          <div className={`col-span-1 lg:col-span-3 space-y-8 transition-all duration-700 ease-out delay-200 ${isVisible ? 'opacity-100 translate-x-0' : 'opacity-0 translate-x-6'}`}>
            <div className="space-y-6">
              <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
                <h2 className="text-lg sm:text-xl md:text-2xl font-bold text-white flex items-center gap-3">
                  <FaTerminal className="text-cyan-400 w-6 h-6" />
                  <span className="text-cyan-400 font-mono">skills.filter()</span>
                </h2>
                <div className="flex items-center gap-2 lg:hidden">
                  <button onClick={() => handleScrollControl('left')} className="p-2 bg-slate-800/60 border border-slate-700 rounded-lg text-slate-400 hover:text-cyan-400 hover:border-cyan-400 transition-all duration-300" aria-label="Scroll left"><FaAnglesLeft className="w-4 h-4" /></button>
                  <button onClick={toggleAutoScroll} className="p-2 bg-slate-800/60 border border-slate-700 rounded-lg text-slate-400 hover:text-cyan-400 hover:border-cyan-400 transition-all duration-300" aria-label={isAutoScrolling ? 'Pause auto-scroll' : 'Resume auto-scroll'}>{isAutoScrolling ? <FaPause className="w-4 h-4" /> : <FaPlay className="w-4 h-4" />}</button>
                  <button onClick={() => handleScrollControl('right')} className="p-2 bg-slate-800/60 border border-slate-700 rounded-lg text-slate-400 hover:text-cyan-400 hover:border-cyan-400 transition-all duration-300" aria-label="Scroll right"><FaAnglesRight className="w-4 h-4" /></button>
                </div>
              </div>

              <div className="flex overflow-x-auto gap-2 sm:gap-3 pb-2 scrollbar-hide" role="tablist">
                {skillCategories.map((category) => (
                  <button key={category.id} onClick={() => handleCategoryChange(category.id)}
                    className={`flex items-center gap-2 px-3 sm:px-4 py-2 sm:py-2.5 rounded-lg sm:rounded-xl font-medium text-xs sm:text-sm transition-all duration-300 whitespace-nowrap shadow-lg min-w-fit flex-shrink-0 ${
                      activeSkillCategory === category.id
                        ? `bg-${category.color}-500 text-slate-900 shadow-${category.color}-500/25`
                        : 'bg-slate-800/60 text-slate-300 border border-slate-700 hover:border-cyan-400 hover:text-cyan-400'
                    }`}
                    role="tab" aria-selected={activeSkillCategory === category.id} aria-label={`Filter by ${category.name}`}
                  >
                    <div className="w-3 h-3 sm:w-4 sm:h-4 flex-shrink-0">{category.icon}</div>
                    <span className="xs:inline">{category.name}</span>
                  </button>
                ))}
              </div>

              <div className="relative">
                <div className="block lg:hidden">
                  <div ref={scrollContainerRef} className="flex gap-5 py-4 px-1 overflow-x-auto scrollbar-hide scroll-smooth" role="tabpanel">
                    {filteredSkills.map((skill) => <SkillCard key={skill.id} skill={skill} isHovered={hoveredSkill === skill.id} onHover={setHoveredSkill} />)}
                  </div>
                </div>
                <div className="hidden lg:block">
                  <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-3 xl:grid-cols-4 2xl:grid-cols-5 gap-5 py-4" role="tabpanel">
                    {filteredSkills.map((skill) => <SkillCard key={skill.id} skill={skill} isHovered={hoveredSkill === skill.id} onHover={setHoveredSkill} />)}
                  </div>
                </div>
              </div>

              <div className="text-center">
                <div className="inline-flex items-center gap-3 text-sm text-slate-400 bg-slate-800/40 px-6 py-3 rounded-full border border-slate-700/50">
                  <FaTerminal className="w-4 h-4 text-cyan-400" />
                  <span>{filteredSkills.length} skills loaded</span>
                  <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse" />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <style jsx>{`
        /* ... Your existing styles ... */
        .scrollbar-hide { -ms-overflow-style: none; scrollbar-width: none; }
        .scrollbar-hide::-webkit-scrollbar { display: none; }

        button:focus-visible, [role="button"]:focus-visible { outline: 2px solid #06b6d4; outline-offset: 2px; }

        @media (min-width: 475px) { .xs\\:inline { display: inline; } }
        @media (max-width: 475px) {
          .xs\\:inline { display: none; }
          [role="tablist"] button { min-width: 2.5rem; justify-content: center; padding: 0.5rem; }
          [role="tablist"] button span:not(.opacity-70) { display: none; }
        }
      `}</style>
    </section>
  );
};

export default QualificationsSection;
