import React, { useState, useEffect } from 'react';
import { Mail, ArrowRight, Code, Terminal, Zap, Github, ExternalLink } from 'lucide-react';
import heroImg from '../assets/b.png'; // Make sure the path is correct

const HeroSection = ({ scrollToSection }) => {
  const role = 'Full Stack Developer    •    UI/UX Designer    •    Problem Solver';

  useEffect(() => {
    const handleMouseMove = (event) => {
      const newX = event.clientX;
      const newY = event.clientY;
      setMousePosition({ x: newX, y: newY });

      // Create flowing bubbles at cursor position
      const newBubble = {
        id: Date.now() + Math.random(),
        x: newX,
        y: newY,
        size: Math.random() * 20 + 10, // Random size between 10-30px
        opacity: Math.random() * 0.8 + 0.2, // Random opacity 0.2-1.0
        color: ['cyan', 'blue', 'purple', 'teal', 'indigo'][Math.floor(Math.random() * 5)],
        duration: Math.random() * 3000 + 2000, // Random duration 2-5 seconds
        createdAt: Date.now()
      };

      setBubbles(prev => [...prev.slice(-15), newBubble]); // Keep only last 15 bubbles
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  // Clean up expired bubbles
  useEffect(() => {
    const interval = setInterval(() => {
      setBubbles(prev => prev.filter(bubble => 
        Date.now() - bubble.createdAt < bubble.duration
      ));
