import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { 
  GraduationCap, Briefcase, Code, Star, Cpu, PenTool, Database, Terminal, 
  Award, Calendar, MapPin, Zap, BookOpen, Target, Trophy, Palette, Cloud, 
  Settings, ChevronLeft, ChevronRight, Play, Pause 
} from 'lucide-react';

// Enhanced Data with better structure
const educationData = [
  {
    id: 'lpu-btech',
    degree: "Bachelor of Technology, CSE",
    institution: "Lovely Professional University",
    period: "2023 – 2027",
    status: "Current",
    location: "Punjab, India",
    icon: <GraduationCap />,
    description: "Specializing in Computer Science and Engineering with focus on Full Stack Development",
    grade: "Current CGPA: 7.1/10",
    color: "cyan"
  },
  {
    id: 'lt-internship',
    degree: "Software Engineering Internship",
    institution: "Larsen & Toubro",
    period: "2025 – Present",
    status: "Active",
    location: "Faridabad, Haryana",
    icon: <Briefcase />,
    description: "Working on enterprise-level applications and gaining hands-on industry experience",
    grade: "Performance: Excellent",
    color: "green"
  }
];

const skillsData = [
  { id: 'react', name: "React & Next.js", icon: <Code />, level: 90, category: "frontend", description: "Building modern web applications" },
  { id: 'node', name: "Node.js & Express", icon: <Cpu />, level: 85, category: "backend", description: "Server-side development" },
  { id: 'javascript', name: "JavaScript (ES6+)", icon: <Code />, level: 95, category: "language", description: "Core programming language" },
  { id: 'typescript', name: "TypeScript", icon: <Code />, level: 80, category: "language", description: "Type-safe development" },
  { id: 'tailwind', name: "Tailwind CSS", icon: <PenTool />, level: 90, category: "frontend", description: "Utility-first CSS framework" },
  { id: 'uiux', name: "UI/UX Design", icon: <PenTool />, level: 75, category: "design", description: "User interface design" },
  { id: 'mongodb', name: "MongoDB & SQL", icon: <Database />, level: 85, category: "database", description: "Database management" },
  { id: 'git', name: "Git & GitHub", icon: <Code />, level: 90, category: "tools", description: "Version control systems" },
  { id: 'docker', name: "Docker", icon: <Cpu />, level: 70, category: "devops", description: "Containerization platform" },
  { id: 'aws', name: "AWS", icon: <Cloud />, level: 65, category: "cloud", description: "Cloud computing services" },
  { id: 'python', name: "Python", icon: <Terminal />, level: 88, category: "language", description: "Versatile programming language" },
  { id: 'java', name: "Java", icon: <Terminal />, level: 82, category: "language", description: "Object-oriented programming" },
